INSERT INTO `brands` (`id`, `name`, `logo`, `top`, `slug`, `meta_title`, `meta_description`, `created_at`, `updated_at`) VALUES
(5, 'Samsung', 'uploads/brands/OOFWh1p54wkDIZVG9fio1Nm0WYeUlixOp60Ic9gZ.jpeg', 1, 'Samsung-TSqBH', 'Samsung', 'Samsung, South Korean company that is one of the world’s largest producers of electronic devices. Samsung specializes in the production of a wide variety of consumer and industry electronics, including appliances, digital media devices, semiconductors, memory chips, and integrated systems. It has become one of the most-recognizable names in technology and produces about a fifth of South Korea’s total exports.', '2020-07-19 00:27:58', '2020-08-29 04:49:42'),
(6, 'Apple', 'uploads/brands/RxQniWdSf1rQ221ckB5nFZRBZKOWVyKSxEfAwwdQ.png', 1, 'Apple-Inc-4Kq24', 'Apple Inc', 'Apple Inc. is an American multinational technology company headquartered in Cupertino, California, that designs, develops, and sells consumer electronics, computer software, and online services. It is considered one of the Big Tech technology companies, alongside Amazon, Google, Microsoft and Facebook.', '2020-07-19 00:31:08', '2020-11-22 22:01:29'),
(7, 'Nokia', 'uploads/brands/BdJ8ucl2rXyJMKyT3HGhLJoD22ycM2yia3trUinr.jpeg', 0, 'Nokia-42BhB', 'Nokia', 'Nokia Corporation is a Finnish multinational telecommunications, information technology, and consumer electronics company, founded in 1865. Nokia\'s headquarters are in Espoo, in the greater Helsinki metropolitan area.', '2020-07-19 02:11:00', '2020-07-24 12:06:31'),
(8, 'LG', 'uploads/brands/DGVyNXgoDexn251i7wQPszO6pl0XfeAnEkQQ2DsC.png', 0, 'LG-8TtyH', 'LG', NULL, '2020-07-19 02:49:41', '2020-07-24 12:06:16'),
(9, 'Xiaomi', 'uploads/brands/Zf388v3xYy0k8ssHA7M6x0wqIcF9xhI27UeN6GyI.png', 1, 'Xiaomi-kGTtc', 'Xiaomi', 'Xiaomi Corporation is a Chinese electronics company founded in April 2010 and headquartered in Beijing. Xiaomi makes and invests in smartphones, mobile apps, laptops, bags, earphones, shoes, fitness bands, and many other products', '2020-07-19 03:20:04', '2020-08-29 04:49:42'),
(11, 'Canon', 'uploads/brands/FeUDyX0wK2iY1AwrXaWjAgDLYU1zfx0VRn5CxWzV.png', 0, 'Canon-r005h', 'Canon', 'Canon Inc. is a Japanese multinational corporation specializing in the manufacture of imaging and optical products, including cameras, camcorders, professional displays, TV broadcasting and film equipment', '2020-07-19 03:29:06', '2020-07-24 12:05:57'),
(12, 'Sony', 'uploads/brands/geQ7kdCfTm0IsMBWKezsxkWXcrzBOjdRKiUrObBy.png', 0, 'Sony-oEORD', 'Sony', 'Sony Corporation is a Japanese multinational conglomerate corporation headquartered in Kōnan, Minato, Tokyo.', '2020-07-19 03:30:54', '2020-07-24 12:05:46'),
(13, 'Philips', 'uploads/brands/DREAkpKp6yazQtOXBmKhwBUvRINE6QqpehotMaMs.png', 0, 'Philips-r5xl4', 'Philips', 'Koninklijke Philips N.V. is a Dutch multinational conglomerate corporation headquartered in Amsterdam, formerly one of the largest electronics companies in the world, currently focused in the area of health technology, with other divisions being successfully divested.', '2020-07-20 04:05:35', '2020-07-24 12:00:48'),
(14, 'JBL', 'uploads/brands/CCn0xaqclAoID1h06Ah8hdUaaXJKXGEQLpDOJJcQ.jpeg', 0, 'JBL-exIUm', 'JBL', 'JBL is an American company that manufactures audio equipment, including loudspeakers and headphones.', '2020-07-20 04:16:14', '2020-07-24 11:59:26'),
(15, 'OnePlus', 'uploads/brands/sIvH8fIwj0uLC5LdtKHgZijwRNMTybfHE5h1olkg.jpeg', 0, 'OnePlus-TtbFh', 'OnePlus', 'One Plus Technology Co., Ltd., commonly referred to as OnePlus, is a Chinese smartphone manufacturer based in Shenzhen, Guangdong. It was founded by Pete Lau and Carl Pei in December 2013. The company officially serves 34 countries and regions around the world as of July 2018.', '2020-07-20 12:08:17', '2020-07-20 12:08:17'),
(16, 'Acer', 'uploads/brands/U6z2zpdcLCcv6ncM091rtO88Nf21hjE6uujeRN9L.png', 0, 'Acer-OML3P', 'Acer', 'Acer Inc. is a Taiwanese multinational hardware and electronics corporation specializing in advanced electronics technology, headquartered in Xizhi, New Taipei City.', '2020-07-21 12:31:00', '2020-07-21 12:31:00'),
(17, 'Realme', 'uploads/brands/RQVPiLweELgz7Walo001ItTa4dZP9w7qTPVKtbze.jpeg', 0, 'Realme-EOetb', 'Realme', 'Realme is a smartphone company based in Shenzhen, Guangdong, China. The company also produces other products such as televisions and headphones', '2020-07-27 14:41:48', '2020-07-27 14:41:48'),
(18, 'HP', 'uploads/brands/rKVjc3cnIKVkrKO8aym3MoJMyZX9mzTYxRnzl73y.png', 0, 'HP-UFBGn', 'HP', 'The Hewlett-Packard Company, commonly shortened to Hewlett–Packard or HP, (/ˈhjuːlɪt ˈpækərd/ HEW-lit PAK-ərd), was an American multinational information technology company headquartered in Palo Alto, California,', '2020-08-06 13:28:28', '2020-08-06 13:28:28'),
(19, 'Itel', 'uploads/brands/uaDArerngXZVYHBNEhT5CMWXalz19seIWjxld9iJ.png', 0, 'Itel-oPmRK', 'Itel Mobile', 'tel Mobile is a Chinese mobile phone and electrical accessories manufacturing company. It is a subsidiary of Transsion, which is headquartered in Shenzhen.[1]\r\n\r\nItel products are mostly sold in Africa and some part of Europe and Latin America. The firm was founded by Lei Weiguo and Shenzhen Transsion Holdings Co Limited in March 2014.', '2020-11-09 06:22:23', '2020-11-09 06:22:23'),
(20, 'Vivo', 'uploads/brands/Qu6lJA6st3VjhDVKHGxxNhFZ7haFJulVKfr8l9XW.png', 1, 'Vivo-us5BL', 'Vivo Mobile', 'Vivo is a Chinese multinational manufacturer of smartphones headquartered in Dongguan, Guangdong province, China. Founded in 1995, vivo entered the telecommunication and consumer electronics industry with landline phones and wireless phones. ... Vivo is now among the top five most profitable smartphone brands in China.', '2020-11-09 06:25:08', '2020-11-22 22:01:29'),
(21, 'Tecno', 'uploads/brands/nq7HKlErk8rbqxy6qAIoXTaxrgyL8YJRx70hAOuZ.png', 0, 'Tecno-2fj9A', 'Tecno', NULL, '2020-11-09 06:32:45', '2020-11-09 06:32:45');
